window.onload = function () {
    var logoutLink = document.getElementById("logout");

    if (logoutLink != null) {
        logoutLink.onclick = function () {
            console.log("Logout");
            console.log("Removendo token do usu√°rio salvo no localStorage...");
            localStorage.removeItem('userToken');

            console.log("Redireciona para home")
            window.location.href = "index.html";

            return false;
        }
    }

    var loggedInElements = this.document.getElementsByClassName("loggedIn");
    var loggedOutElements = this.document.getElementsByClassName("loggedOut");


    if (isLoggedIn()) {
        for (const value of loggedInElements) {
            value.classList.remove("d-none");
        }
        for (const value of loggedOutElements) {
            value.classList.add("d-none");
        }
    }
}

function isLoggedIn() {
    if (localStorage.userToken == null) {
        return false;
    } else {
        return true;
    }
}